(185, 'Various Pages[span class="rt-title-tag"]Flexible and adaptable sample pages[/span]', '', '<p class="rt-center promo2 largemargintop largepaddingtop">See a range of example layouts possible with Spectral, through its responsive layout and the Gantry Framework grid system</p>\r\n\r\n<div class="clear"></div>', 1, 'showcase-a', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 1, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"rt-title-promo","cache":"1","cache_time":"900","cachemode":"static"}', 0, '*'),
(186, 'Pages Chart A', '', '<canvas id="myChart-a" height="320" width="600"></canvas>\r\n\r\n<script type="text/javascript">\r\n	var lineChartData = {\r\n		labels : ["Cerulean","Chapelco","Lumiere","Hexeris","Oculus","Stratos","Corvus"],\r\n		datasets : [\r\n			{\r\n				fillColor : "transparent",\r\n				strokeColor : "#97DBF2",\r\n				pointColor : "#97DBF2",\r\n				pointStrokeColor : "#97DBF2",\r\n				data : [600,500,900,800,500,400,700]\r\n			},\r\n			{\r\n				fillColor : "transparent",\r\n				strokeColor : "#DE4E33",\r\n				pointColor : "#DE4E33",\r\n				pointStrokeColor : "#DE4E33",\r\n				data : [200,400,600,300,900,200,500]\r\n			},		\r\n		]\r\n	},\r\n	options = {\r\n		//Boolean - If we want to override with a hard coded scale\r\n		scaleOverride : true,\r\n		\r\n		//** Required if scaleOverride is true **\r\n		//Number - The number of steps in a hard coded scale\r\n		scaleSteps : 9,\r\n		//Number - The value jump in the hard coded scale\r\n		scaleStepWidth : 100,\r\n		//Number - The scale starting value\r\n		scaleStartValue : 100,\r\n\r\n		//String - Scale label font colour	\r\n		scaleFontColor : "rgba(0,0,0,0.3)",\r\n\r\n		//String - Colour of the scale line\r\n		scaleLineColor : "rgba(0,0,0,0.3)",\r\n\r\n		//String - Colour of the grid lines\r\n		scaleGridLineColor : "rgba(0,0,0,0.3)",	\r\n\r\n		//Boolean - Whether the line is curved between points\r\n		bezierCurve : false,	\r\n\r\n		//Number - Radius of each point dot in pixels\r\n		pointDotRadius : 8,\r\n\r\n		//Number - Pixel width of point dot stroke\r\n		pointDotStrokeWidth : 5,\r\n		\r\n		//Number - Pixel width of dataset stroke\r\n		datasetStrokeWidth : 6,\r\n		\r\n		//Boolean - Whether to fill the dataset with a colour\r\n		datasetFill : true,\r\n\r\n	}\r\n\r\n	//Get the context of the canvas element we want to select\r\n	var ctx = document.getElementById("myChart-a").getContext("2d");\r\n	var myNewChart = new Chart(ctx).Line(lineChartData, options);\r\n\r\n</script>', 1, 'feature-a', 0, '0000-00-00 0