(187, 'Pages Chart B', '', '<canvas id="myChart-b" height="320" width="600"></canvas>\r\n\r\n<script type="text/javascript">\r\n	var barChartData = {\r\n		labels : [""],\r\n		datasets : [\r\n			{\r\n				fillColor : "#D95240",\r\n				strokeColor : "#D95240",\r\n				data : [300]\r\n			},\r\n			{\r\n				fillColor : "#6FC6A0",\r\n				strokeColor : "#6FC6A0",\r\n				data : [800]\r\n			},\r\n			{\r\n				fillColor : "#D6A842",\r\n				strokeColor : "#D6A842",\r\n				data : [600]\r\n			},\r\n			{\r\n				fillColor : "#6FB4C5",\r\n				strokeColor : "#6FB4C5",\r\n				data : [1000]\r\n			},\r\n			{\r\n				fillColor : "#D49245",\r\n				strokeColor : "#D49245",\r\n				data : [400]\r\n			}									\r\n		]\r\n	},\r\n	options = {\r\n		//Boolean - If we want to override with a hard coded scale\r\n		scaleOverride : true,\r\n		\r\n		//** Required if scaleOverride is true **\r\n		//Number - The number of steps in a hard coded scale\r\n		scaleSteps : 9,\r\n		//Number - The value jump in the hard coded scale\r\n		scaleStepWidth : 100,\r\n		//Number - The scale starting value\r\n		scaleStartValue : 100,\r\n\r\n		//String - Scale label font colour	\r\n		scaleFontColor : "rgba(0,0,0,0.3)",\r\n\r\n		//String - Colour of the scale line\r\n		scaleLineColor : "rgba(0,0,0,0.3)",\r\n\r\n		//String - Colour of the grid lines\r\n		scaleGridLineColor : "rgba(0,0,0,0.3)",	\r\n\r\n		//Number - Spacing between data sets within X values\r\n		barDatasetSpacing : 30,	\r\n	}\r\n\r\n	//Get the context of the canvas element we want to select\r\n	var ctx = document.getElementById("myChart-b").getContext("2d");\r\n	var myNewChart = new Chart(ctx).Bar(barChartData, options);\r\n\r\n</script>', 1, 'feature-b', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"title1","cache":"1","cache_time":"900","cachemode":"static"}', 0, '*'),
(188, 'Chart.js', '', '<p><strong>NOTE: </strong>The canvas only works on modern browsers (Firefox, Opera, Chrome, Safari, and Internet Explorer 9+) that support the HTML5 canvas element. <a href="http://www.w3schools.com/html/html5_canvas.asp" target="_blank">Internet Explorer 8 and earlier versions, do not support the canvas element.</a></p>\r\n\r\n<p>The two charts shown above are the sample chart we used on our demo pages. It''s based on <a href="http://www.chartjs.org/">Chart.js</a>. Chart.js is an easy, object oriented client side graphs for designers and developers.</p>\r\n\r\n<p class="nomarginbottom">For more information how to create great looking charts using Chart.js, please visit <a href="http://www.chartjs.org/">Chart.js</a> homepage or download the script <a href="https://github.com/nnnick/Chart.js">here</a>.</p>', 1, 'maintop-a', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 'mod_custom', 1, 0, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"title1","cache":"1","cache_time":"900","cachemode":"static"}', 0, '*'),
